<?phpif (!function_exists('master_addon_categories')):    function master_addon_categories($elements_manager)    {        \Elementor\Plugin::$instance->elements_manager->add_category(            'master-addons',            [                'title' => __('Master Elements ADDONS', 'masterelements'),                'icon' => 'fa fa-plug',            ]        );    }endif;if (!function_exists('master_init_widgets')):    function master_init_widgets()    {        $widgets = register_addons_list();        $aclass = new \MasterElements\Modules\Theme_Builder\Master_Custom_Post();        $active_addons = $aclass->get_main_settings_data();        foreach ($widgets as $widget) {            if (isset($active_addons[$widget['id']]['name']) && $active_addons[$widget['id']]['value'] != 'no') {                if ($active_addons[$widget['id']]['value'] == 'yes') {                    if (is_array($widget['item'])) {                        foreach ($widget['item'] as $wid) {                            require_once \MasterElements::widgets_dir() . $wid['item'] . '/' . $wid['item'] . '.php';                            $class_name = '\Elementor\\' . \MasterElements\Classes\Utils::make_classname('Master_' . $wid['item']);                            \Elementor\Plugin::instance()->widgets_manager->register_widget_type(new $class_name());                        }                    } else {                        require_once \MasterElements::widgets_dir() . $widget['item'] . '/' . $widget['item'] . '.php';                        $class_name = '\Elementor\\' . \MasterElements\Classes\Utils::make_classname('Master_' . $widget['item']);                        \Elementor\Plugin::instance()->widgets_manager->register_widget_type(new $class_name());                    }                }            }        }    }endif;function register_addons_